import React                              from 'react'
import axios                              from 'axios'
import PropTypes                          from 'prop-types'

import { BrowserRouter as Router, Route } from 'react-router-dom'

import Container                          from 'react-bootstrap/Container';
import {Row, Col}                         from 'react-bootstrap/';
import Button                             from 'react-bootstrap/Button';

import { LoginView }                      from '../LoginView/login-view'
import { RegistrationView }               from '../RegistrationView/registration'
import { MovieCard }                      from '../MovieCard/movie-card'
import { MovieView }                      from '../MovieView/movie-view'
import { CarouselView }                   from '../Carousel/carousel'

import './main-view.scss'

export class MainView extends React.Component {
  constructor() {
    super()

    this.state = {
      movies: [],
      user: null
    }
  }

  componentDidMount() {
    let accessToken = localStorage.getItem('token')
    if (accessToken !== null) {
      this.setState({
        user: localStorage.getItem('user')
      })
      this.getMovies(accessToken)
    }
  }

  getMovies(token) {
    axios
      .get('https://myflixdbs-z.herokuapp.com/movies', {
        headers: { Authorization: `Bearer ${token}` }
      })
      .then(res => {
        this.setState({ movies: res.data })
      })
      .catch(function (error) {
        console.log(error)
      })
  }

  onLoggedIn(authData) {
    console.log(authData)
    this.setState({
      user: authData.user.Username
    })
    localStorage.setItem('token', authData.token)
    localStorage.setItem('user', authData.user.Username)
    this.getMovies(authData.token)
  }

  onLogOut() {
    localStorage.removeItem('token')
    localStorage.removeItem('user')
    window.open('/', '_self')
  }

  onMovieClick(movie) {
    this.setState({
      selectedMovie: movie
    })
  }

  onLoggedIn(user) {
    this.setState({
      user
    })
  }

  registerUser() {
    this.setState({
      newUser: true
    })
  }

  userRegistered() {
    this.setState({
      newUser: null
    })
  }

  logOutHandler() {
    localStorage.removeItem('token')
    localStorage.removeItem('user')
  }

  render() {
    const { movies, selectedMovie, user } = this.state

    if (!user) return <LoginView onLoggedIn={user => this.onLoggedIn(user)} />

    if (!movies) return <div className='main-view' />

    return (
      <Router>
        <Container className="main-view">
          <Navbar collapseOnSelect expand="lg" className="navbar-main">
              <Navbar.Brand as={Link} to="/" className="brand-MooVIV">MooVIV!</Navbar.Brand>
              <Navbar.Toggle aria-controls="basic-navbar-nav" />
              <Navbar.Collapse id="basic-navbar-nav">
                <Nav className="mr-auto">
                  <Nav.Link as={Link} to="/" className="navbar-link">Home</Nav.Link>
                  <Nav.Link as={Link} to={`/users/${user}`} className="navbar-link">Profile</Nav.Link>
                <Button onClick={this.onLogOut} variant="dark" type="submit" className="button log-out"> Log Out</Button>
                </Nav>
              </Navbar.Collapse>
            </Navbar>

            <Row>
            <Route exact patch="/" render={() => {
              if (!user) return <LoginView onLoggedIn={user => this.onLoggedIn(user)} />;
                return movies.map(m => 
                <Col key={m._id} className="justify-content-around">
                  <MovieCard key={m._id} movie={m} />
                </Col>
                )
            }}/>
            </Row>

          <Route exact path="/movies/:movieId" render={({match}) => <MovieView movie={movies.find(m => m._id === match.params.movieId)}/>}/>
          <Route exact path="/register" render={() => <RegistrationView />} />
          <Route exact path="/genres/:name" render={({ match }) => {
            if (!movies) return <Container className="main-view"/>
            return <GenreView genre={movies.find(m => m.Genre.Name === match.params.name).Genre}/>
          }}/>
          <Route exact path="/directors/:name" render={({ match}) => {
            if (!movies) return <Container className="main-view"/>
            return <DirectorView director={movies.find(m => m.Director.Name === match.params.name).Director}/>
          }}/>
          <Route exact path="/users/:username" render={({ match}) => {
            if (!user) return <Container className="main-view"/>
            return <ProfileView user={user} movies={movies} />
          }}/>
          <Route exact path="/logout" render={() => <LoginView/>}/>



          </Container>
      </Router>
    )
  }
}

MainView.propTypes = {
  movies: PropTypes.shape({
    Title: PropTypes.string.isRequired,
    Description: PropTypes.string.isRequired,
    ImagePath: PropTypes.string.isRequired
  }),
  selectedMovie: PropTypes.shape({
    Title: PropTypes.string.isRequired,
    Description: PropTypes.string.isRequired,
    ImagePath: PropTypes.string.isRequired
  })
}
