import React from "react";
import axios from "axios";
import PropTypes from "prop-types";
import Container from "react-bootstrap/Container";
import Col from "react-bootstrap/Col";
import Row from "react-bootstrap/Row";
import Button from "react-bootstrap/Button";

import "./main-view.scss";

import {LoginView} from '../LoginView/login-view';
import {RegistrationView} from '../RegistrationView/regis-view';
import {MovieCard} from '../MovieCard/movie-card';
import {MovieView} from '../MovieView/movie-view';
import { BrowserRouter as Router, Route } from "react-router-dom";


export class MainView extends React.Component {

  constructor() {
    super();

    this.state = {
      movies: null,
      selectedMovie: null,
      user: null,
      newUser: null
    };
  }

  componentDidMount() {
    let accessToken = localStorage.getItem('token');
    if (accessToken !== null) {
      this.setState({
        user: localStorage.getItem('user')
      });
      this.getMovies(accessToken);
    }
  }

  getMovies(token) {
    axios.get('https://myflixdbs-z.herokuapp.com/movies', {
      headers: { Authorization: `Bearer ${token}` }
    })
      .then(response => {
        // Assign the result to the state
        this.setState({
          movies: response.data
        });
      })
      .catch(function (error) {
        console.log(error);
      });
  }

  onMovieClick(movie) {
    this.setState({
      selectedMovie: movie
    });
  }


  onLoggedIn(authData) {
    console.log(authData);
    this.setState({
      user: authData.user.Username
    });

    localStorage.setItem("token", authData.token);
    localStorage.setItem("user", authData.user.Username);
    this.getMovies(authData.token);
  }

  registerUser() {
    this.setState({
      newUser: true,
    })
  }

  userRegistered() {
    this.setState({
      newUser: null,
    })
  }

  logOutHandler() {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
  }



  render() {

    const { 
      movies,
      selectedMovie,
      user,
      newUser
    } = this.state;

    if (!user) {
      if (newUser) return (
        <RegistrationView
          userRegistered={() => this.userRegistered()}
          onLoggedIn={user => this.onLoggedIn(user)}
        />
      );

      else return (
        <LoginView
          onLoggedIn={user => this.onLoggedIn(user)}
          newUser={() => this.registerUser()}
        />
      );
    }


    if (!movies) return <div className="main-view" />;

    return (

      <Router>
        <div className="main-view">
          <Container className="main-view-container">
            <Row>
              <Route exact path="/" render={() => movies.map(m =>
                <MovieCard key={m._id} movie={m} />)} />

              <Route path="/movies/:movieId" render={({ match }) =>
                <MovieView movie={movies.find(m => m._id === match.params.movieId)} />} />

              <Route path="/directors/:name" render={({ match }) => {
                if (!movies) return <div className="main-view" />;
                return <DirectorView director={movies.find(m => m.Director.Name === match.params.name).Director} />
              }
              } />
            </Row>
          </Container>
        </div>
      </Router >
    );
  }
}

MainView.propTypes = {
  movies: PropTypes.shape({
    Title: PropTypes.string.isRequired,
    Description: PropTypes.string.isRequired,
    ImagePath: PropTypes.string.isRequired
  }),
  selectedMovie: PropTypes.shape({
    Title: PropTypes.string.isRequired,
    Description: PropTypes.string.isRequired,
    ImagePath: PropTypes.string.isRequired
  })
}
