import axios from 'axios'
import PropTypes from 'prop-types'
import React from 'react'
import { Button, Col, Container, Row } from 'react-bootstrap'
import { Link } from 'react-router-dom'
import './movie-view.scss'

import { Navigation } from '../Navigation/navigation'

export class MovieView extends React.Component {
  constructor () {
    super()

    this.state = {
      movies: [],
      favoriteMovies: []
    }
  }

  addToFavoriteMovies (movie) {
    const username = localStorage.getItem('user')
    const token = localStorage.getItem('token')

    axios
      .post(
        `https://myflixdbs-z.herokuapp.com/users/${username}/Movies/${movie}`,
        {
          FavoriteMovies: this.FavoriteMovies
        },
        {
          headers: { Authorization: `Bearer ${token}` }
        }
      )
      .then(response => {
        this.setState({
          FavoriteMovies: response.data.FavoriteMovies
        })
      })
      .catch(function (error) {
        console.log(error)
      })
    alert('movie successfully added.')
  }

  render () {
    const { movie } = this.props

    if (!movie) return null

    return (
      <div className='view-container'>
        <Navigation />
        <Container className='movie-view-container'>
          <div className='movie-view'>
            <img className='movie-poster' src={movie.ImagePath} />
            <div className='movie-title'>
              <span className='label'>Title: </span>
              <span className='value'>{movie.Title}</span>
            </div>
            <div className='movie-description'>
              <span className='label'>Description: </span>
              <span className='value'> {movie.Description}</span>
            </div>

            <div className='movie-genre'>
              <span className='label'> Genre: </span>
              <span className='value'>{movie.Genre.Name}</span>
            </div>
            <div className='movie-director'>
              <span className='label'>Director: </span>
              <span className='value'>{movie.Director.Name}</span>
            </div>
            <Link to={`/Directors/${movie.Director.Name}`}>
              <Button className='button-director'>Director</Button>
            </Link>
            <Link to={`/`}>
              <Button className=' button-goBack'>Back</Button>
            </Link>
            <Link to={`/genres/${movie.Genre.Name}`}>
              <Button className='button-genre'>{movie.Genre.Name}</Button>
            </Link>
            <Button
              className='button-favorite'
              onClick={() => this.addToFavoriteMovies(movie._id)}
            >
              Add to Favorites
            </Button>
          </div>
        </Container>
      </div>
    )
  }
}

MovieView.propTypes = {
  movie: PropTypes.shape({
    Title: PropTypes.string.isRequired,
    Description: PropTypes.string.isRequired,
    Genre: PropTypes.shape({
      Name: PropTypes.string.isRequired,
      Description: PropTypes.string.isRequired
    }),
    Director: PropTypes.shape({
      Name: PropTypes.string.isRequired,
      Bio: PropTypes.string.isRequired,
      Birth: PropTypes.date,
      Death: PropTypes.date
    }),
    ImagePath: PropTypes.string.isRequired
  })
}
